import React, { forwardRef, useRef, useState } from 'react'
import Counter from './components/Counter'
import PostForm from './components/PostForm';
import PostItem from './components/PostItem';
import PostList from './components/PostList';
import MyButton from './components/UI/button/MyButton'
import MyInput from './components/UI/input/MyInput'
import MySelect from './components/UI/select/MySelect';
import './styles/App.css'

function App() {

  const [posts, setPosts] = useState([
    { id: 1, title: 'React', body: 'Decrip' },
    { id: 2, title: 'React 5', body: 'tions' },
    { id: 3, title: 'React 3', body: 'cripts' }
  ])

  const [selectedSort, setSelectedSort] = useState('')
  const [searchQuery, setSearchQuery] = useState('')
  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }
  const getSortedPost = () => {
    if (selectedSort) {
      return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
    }
    return posts
  }
  const sortedPosts = getSortedPost()
  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  const sortPosts = (sort) => {
    setSelectedSort(sort)

  }


  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }} />

      <div>
        <MyInput
          placeholder="Поиск..."
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)} />
        <MySelect
          value={selectedSort}
          onChang={sortPosts}
          defaultValue={'Sort'}
          options={[
            { value: 'title', name: 'По названию' },
            { value: 'body', name: 'По описанию' }
          ]}
        />


      </div>
      {posts.length !== 0
        ? <PostList remove={removePost} posts={sortedPosts} title='title' />
        : <h1 style={{ textAlign: 'center', color: 'teal' }}>Посты не найдены</h1>}
    </div>
  );
}

export default App;
